import React from "react";
import { makeStyles, createStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import MediaQuery from "react-responsive";

import MsgDisp from "../Container/msgDisp";
import DispPicture from "../Container/dispPicture";
import EntryBtn from "../Container/Btn/entryBtn";
import LoginBtn from "../Container/Btn/loginBtn";

const useStyle = makeStyles(() =>
  createStyles({
    msgDisp: {
      gridRow: 2,
      gridColumn: 2,
      height: "100%",
    },
    picDisp: {
      gridRow: 4,
      gridColumn: 2,
      height: "100%",
    },
    buttonBoxPC: {
      justifyContent: "center",
      gridRow: 6,
      gridColumn: 2,
      display: "grid",
      gridTemplateColumns: "40% 20% 40%",
    },
    buttonBoxPhone: {
      justifyContent: "center",
      gridRow: 6,
      gridColumn: 2,
      display: "grid",
      gridTemplateColumns: "40% 20% 40%",
    },
    entryPC: {
      gridColumn: 1,
    },
    entryPhone: {
      gridColumn: 1,
    },
    loginPhone: {
      gridColumn: 3,
    },
  })
);

type HomeProps = {
  isMax: boolean;
  setIsMax: React.Dispatch<React.SetStateAction<boolean>>;
};

const Home: React.FC<HomeProps> = (props: HomeProps) => {
  const { isMax = false, setIsMax = () => undefined } = props;
  const styles = useStyle();
  return (
    <>
      <MediaQuery query="(min-width:767px)">
        <Box className={styles.msgDisp}>
          <MsgDisp isMax={isMax} />
        </Box>
        <Box className={styles.picDisp}>
          <DispPicture />
        </Box>
        <Box className={styles.buttonBox}>
          <Box className={styles.entry}>
            <EntryBtn isMax={isMax} setIsMax={setIsMax} />
          </Box>
          <Box className={styles.login}>
            <LoginBtn />
          </Box>
        </Box>
      </MediaQuery>
      <MediaQuery query="(max-width:767px)">
        <Box className={styles.msgDisp}>
          <MsgDisp isMax={isMax} />
        </Box>
        <Box className={styles.picDisp}>
          <DispPicture />
        </Box>
        <Box className={styles.buttonBox}>
          <Box className={styles.entry}>
            <EntryBtn isMax={isMax} setIsMax={setIsMax} />
          </Box>
          <Box className={styles.login}>
            <LoginBtn />
          </Box>
        </Box>
      </MediaQuery>
    </>
  );
};

export default Home;
